import React from "react";
import PropTypes from "prop-types";

const Book = ({ changeBookShelf, book }) => {
  let image ;
   if(image==book.imageLinks){
    image=book.imageLinks.thumbnail
  }
  else{
    image="https://leadershiftinsights.com/wp-content/uploads/2019/07/no-book-cover-available.jpg";
  }
  return(
    <li>
      <div className="book">
        <div className="bookTop">
          <div
            className="bookCover"
            className="book-cover"
            style={{
              width: 128,
              height: 193,
              backgroundImage: `url(${image})`
            }}
          />
          <div className="book-shelf-changer">
            <select value={book.shelf} onChange={event=> changeBookShelf(book, event.target.value)}>
            <option disabled>Move to...</option>
            <option value="currentlyReading">Currently Reading</option>
            <option value="wantToRead">Want to Read</option>
            <option value="read">Read</option>
            <option value="none">None</option>
            </select>
          </div>
        </div>
        <div className="book-title">{book.title}</div>
        <div className="book-authors">
        {book.authors?book.authors.join(" ")
        :"Author unknown"
        }
        </div>
        </div>
      </li>

  );
};

Book.propTypes = {
  changeBookShelf: PropTypes.func.isRequired,
  book: PropTypes.object.isRequired
};

export default Book;
